Item.method_code:
switch (this.value) {
    case "Add Row":
        document.NodeEditor.add_row();
        break;

    case "Add Column":
        document.NodeEditor.add_col();
        break;

    case "Remove Row":
        document.NodeEditor.remove_row();
        break;

    case "Remove Column":
        document.NodeEditor.remove_col();
        break;
}

// update template_definition
document.thisItem.setProperty("template_definition", JSON.stringify(document.ViewCard.obj, null, "\t"));
